function initLightbox() {
	jQuery(document).ready(function() {	
		function createCookie(name, value, days) {
			var cookie = name + "=" + escape(value) + ";";
			var expires = new Date(new Date().getTime() + parseInt(days) * 1000 * 60 * 60 * 24);
			cookie += " expires=" + expires.toGMTString() + ";";
			cookie += " path=/;";
			cookie += " domain=www.tvinsider.com;";
			document.cookie = cookie;
		}
		function getCookie(name) {
			var regexp = new RegExp("(?:^" + name + "|;\s*"+ name + ")=(.*?)(?:;|$)", "g");
			console.log("regexp: "+regexp);
			var result = regexp.exec(document.cookie);
			console.log("result: "+result);
			return (result === null) ? null : result[1];
		}
		function readCookie(name) {
			var nameEQ = name + "=";
			var ca = document.cookie.split(';');
			for(var i=0;i < ca.length;i++) {
		    	var c = ca[i];
				while (c.charAt(0)==' ') c = c.substring(1,c.length);
					if (c.indexOf(nameEQ) == 0) {
						return c.substring(nameEQ.length,c.length);
					}
		  	}
		  	return null;
		}
		function checkCookie() {
			if (readCookie("interstitial") === null) {
				showInterstial(); 
				createCookie("interstitial", "newsletter", 3);
			} else {
			};
		}
  		function deleteCookie(name, path, domain) {
			if (getCookie(name)) createCookie(name, "", -1);
		}
		function showInterstial() { 
			ga('send', 'event', 'Lightbox', 'Promo View', {'nonInteraction': 1});
			jQuery('#mc-embedded-subscribe-form').on('submit', function() {
				ga('send', 'event', 'Lightbox', 'Promo Subscribe');
			});
			var id = '#dialog';
			//Get the screen height and width
			var maskHeight = jQuery(document).height();
			var maskWidth = jQuery(window).width();
			//Set heigth and width to mask to fill up the whole screen
			jQuery('#mask').css({'width':maskWidth,'height':maskHeight});
			//transition effect		
			jQuery('#mask').fadeIn(500);	
			jQuery('#mask').fadeTo("slow",0.8);	
			//Get the window height and width
			var winH = jQuery(window).height();
			var winW = jQuery(window).width();
			//Set the popup window to center
			jQuery(id).css('top',  winH/2-jQuery(id).height()/2);
			jQuery(id).css('left', winW/2-jQuery(id).width()/2);
			//transition effect
			jQuery(id).fadeIn(500); 	
			jQuery('.window #close-box').click(function (e) {
				e.preventDefault();
				jQuery('#mask').hide();
				jQuery('.window').hide();
			});		
			//if mask is clicked
			jQuery('#mask').click(function () {
				jQuery(this).hide();
				jQuery('.window').hide();
			});		
		}
		if (instl == true) {
			checkCookie();
		}
	});
}


//social popups
function soc(platform) {
	var width = 500;
	var height = 300;
    var leftPosition = (window.screen.width / 2) - ((width / 2) + 10);
    var topPosition = (window.screen.height / 2) - ((height / 2) + 50);
    var wndw = "status=no,height=" + height + ",width=" + width + ",resizable=yes,left=" + leftPosition + ",top=" + topPosition + ",screenX=" + leftPosition + ",screenY=" + topPosition + ",toolbar=no,menubar=no,scrollbars=no,location=no,directories=no";
    var u = location.href;
    var t = document.title;
    var w = encodeURIComponent(t.split("â€“")[0]);
    if (platform == "fb") {
	    window.open('http://www.facebook.com/sharer.php?u='+u,'sharer', wndw);
    } else if (platform == "tw") {
    	window.open('http://twitter.com/share?url='+u+'&text='+w+'@tvinsider','sharer', wndw);
    } else if (platform == "go") {
    	window.open('https://plus.google.com/share?url='+u,'sharer', wndw);
    } else if (platform == "em") {
    	window.open('mailto:?subject=TVInsider&body='+u,'sharer', wndw);
    } 
    return false;
}


