// scroll galleries init
function initCarousel() {
	var win = jQuery(window);
	jQuery('.main-slideshow').each(function() {
		var gallery = jQuery(this);
		gallery.find('.slide').each(function(i) {
			var slide = jQuery(this);
			var label = slide.find('.single-label');
			label.attr('data-index', i).appendTo(gallery);
		});
		var labels = gallery.find('.single-label');
		function refreshLabels(obj) {
			var activeLabel = labels.filter('[data-index="' + obj.currentStep + '"]');
			labels.not(activeLabel).fadeOut('fast');
			activeLabel.fadeIn('fast');
		}
		function setPageCount() {
			var totalslides = jQuery("#slideset div.slide").length;
			var currentslide = jQuery('#slideset div.active').prevAll().length + 1;
			jQuery('.pagination').html(currentslide + " of " + totalslides);
		}
		imagesLoaded(gallery, function() {
			gallery.scrollGallery({
				mask: '.mask',
				slider: '.slideset',
				slides: '.slide',
				btnPrev: 'a.btn-prev',
				btnNext: 'a.btn-next',
				generatePagination: false,
				stretchSlideToMask: true,
				maskAutoSize: true,
				autoRotation: false,
				switchTime: 5000,
				animSpeed: 500,
				step: 1,
				onInit: function(obj) {
					refreshLabels(obj);
					setPageCount();
					win.trigger('refresh');
				},
				onChange: function() {
					setPageCount();
					//triggers google ads
					googletag.pubads().refresh([slot_refresh1]);
					googletag.pubads().refresh([slot_refresh2]);
					win.trigger('refresh');
					//adds hash to url
					var currentslide = jQuery('#slideset div.active').prevAll().length + 1;
					window.location.hash = currentslide;
					//triggers google analytics
					ga('send', 'pageview', { 'page': location.pathname + location.search + location.hash});
				},
				onBeforeChange: refreshLabels
			});
		});
	});
	jQuery('.main-carousel').each(function() {
		var gallery = jQuery(this);
		gallery.find('.slide').each(function(i) {
			var slide = jQuery(this);
			var label = slide.find('.single-label');
			label.attr('data-index', i).appendTo(gallery);
		});
		var labels = gallery.find('.single-label');
		function refreshLabels(obj) {
			var activeLabel = labels.filter('[data-index="' + obj.currentStep + '"]');
			labels.not(activeLabel).fadeOut('fast');
			activeLabel.fadeIn('fast');
		}
		imagesLoaded(gallery, function() {
			gallery.scrollGallery({
				mask: '.mask',
				slider: '.slideset',
				slides: '.slide',
				btnPrev: 'a.btn-prev',
				btnNext: 'a.btn-next',
				generatePagination: '.pagination',
				stretchSlideToMask: true,
				maskAutoSize: true,
				autoRotation: true,
				switchTime: 5000,
				animSpeed: 500,
				step: 1,
				onInit: function(obj) {
					refreshLabels(obj);
					win.trigger('refresh');
				},
				onChange: function() {
					win.trigger('refresh');
				},
				onBeforeChange: refreshLabels
			});
		});
	});
	jQuery('.aside-carousel').each(function() {
		var block = jQuery(this);
		imagesLoaded(block, function() {
			block.scrollGallery({
				mask: '.mask',
				slider: '.slideset',
				slides: '.slide',
				btnPrev: 'a.btn-prev',
				btnNext: 'a.btn-next',
				generatePagination: '.pagination',
				stretchSlideToMask: true,
				autoRotation: false,
				switchTime: 3000,
				animSpeed: 500
			});
		});
	});
	jQuery('.video-carousel').each(function() {
		var block = jQuery(this);
		var activeClass = 'active';
		var iframe = jQuery('.video-box iframe');
		imagesLoaded(block, function() {
			block.scrollGallery({
				mask: '.mask',
				activeClass: 'active-slide',
				slider: '.slideset',
				slides: '.slide',
				btnPrev: 'a.btn-prev',
				btnNext: 'a.btn-next',
				pagerLinks: '.pagination li',
				autoRotation: false,
				switchTime: 3000,
				animSpeed: 500,
				onInit: function(obj) {
					var links = obj.slides.find('a');
					links.each(function() {
						var link = jQuery(this);
						var newSrc = link.attr('href');
						if (link.hasClass(activeClass)) {
							iframe.attr('src', newSrc);
						}
						link.on('click', function(e) {
							e.preventDefault();
							if (!link.hasClass(activeClass)) {
								links.removeClass(activeClass);
								link.addClass(activeClass);
								iframe.attr('src', newSrc);
							}
						});
					});
				}
			});
		});
	});
}

