// popups init
function initPopups() {
	jQuery('#header').contentPopup({
		mode: 'click',
		popup: '.search-form',
		btnOpen: '.open-search',
		hideOnClickOutside: false
	});
	jQuery('.filter-list li').contentPopup({
		mode: 'click',
		popup: '.drop-filter'
	});
	jQuery('#footer .search-popup').contentPopup({
		mode: 'click',
		popup: '.search-form',
		btnOpen: '.open-search',
		hideOnClickOutside: false
	});
	jQuery('.season-popup').contentPopup({
		mode: 'click',
		popup: '.drop',
		onInit: function(obj) {
			var gallery = jQuery('.video-carousel');
			var opener = obj.btnOpen.find('span');
			var links = obj.popup.find('a');
			links.each(function() {
				var link = jQuery(this);
				var text = link.text();
				var value = link.attr('data-value');
				link.on('click', function(e) {
					e.preventDefault();
					opener.text(text);
					obj.hidePopup();
					var galleryInstance = gallery.data('ScrollGallery');
					var slides = galleryInstance.slides;
					var stepSize = galleryInstance.stepSize;
					var activeSlide = slides.filter('[data-season="' + value + '"]').eq(0);
					var slidePosition = activeSlide.position().left;
					var activeIndex = Math.floor(slidePosition / stepSize);
					galleryInstance.numSlide(activeIndex);
					activeSlide.find('a').trigger('click');
				});
			});
		}
	});
}

