/*
 * jQuery Fixed Block plugin
 */
;(function($) {
	function FixedBlock(options) {
		this.options = $.extend({
			header: '#header',
			fixedClass: 'fixed-position',
			delay: 500,
			extraTop: 0,
			panel: '.panel',
			placeholder: true,
			destroyOn: 0
		}, options);
		this.init();
	}
	FixedBlock.prototype = {
		init: function() {
			this.findElements();
			this.attachEvents();
			this.makeCallback('onInit', this);
		},
		findElements: function() {
			var self = this;
			this.win = $(window);
			this.header = $(this.options.header);
			this.area = $(this.options.holder);
			this.panel = this.area.find(this.options.panel);
		},
		attachEvents: function() {
			var self = this;
			this.resizeHandler = function() {
				self.refreshOffset();
				clearTimeout(self.timer);
				self.timer = setTimeout(function() {
					self.refreshOffset();
				}, self.options.delay);
			};
			this.scrollHandler = function() {
				self.checkOffset();
			};
			if (this.panel && this.panel.length) {
				this.resizeHandler();
				this.win.on({
					refresh: this.resizeHandler,
					load: this.resizeHandler,
					resize: this.resizeHandler,
					orientationchange: this.resizeHandler,
					scroll: this.scrollHandler
				});
			}
		},
		checkOffset: function() {
			var self = this;
			var offsetRange = Math.min((self.areaHeight + self.areaOffset) - (self.win.scrollTop() + self.winHeight / 2), 0);
			if (self.win.scrollTop() >= self.fixedPosition && self.panel.is(':visible') && self.options.destroyOn < window.innerWidth) {
				self.panel.addClass(self.options.fixedClass).css({
					right: 'auto',
					width: '',
					left: self.panelOffset.left,
					top: self.headerHeight + self.options.extraTop + offsetRange
				});
				if (self.options.placeholder) {
					self.panel.css({ width: this.panelWidth });
					self.panel.parent().css({ paddingTop: self.panelHeight });
				}
			} else {
				self.panel.removeClass(self.options.fixedClass).removeAttr('style');
				if (self.options.placeholder) {
					self.panel.css({ width: '' });
					self.panel.parent().removeAttr('style');
				}
			}
		},
		refreshOffset: function() {
			this.panel.removeClass(this.options.fixedClass).removeAttr('style');
			if (this.options.placeholder) this.panel.parent().removeAttr('style');
			this.winHeight = this.win.height();
			this.areaHeight = this.area.outerHeight();
			this.areaOffset = this.area.offset().top;
			this.headerHeight = this.header.outerHeight();
			this.panelOffset = this.panel.offset();
			this.panelWidth = this.panel.outerWidth();
			this.panelHeight = this.panel.outerHeight(true);
			this.fixedPosition = this.panelOffset.top - this.headerHeight - this.options.extraTop;
			this.scrollHandler();
		},
		destroy: function() {
			this.win.off({
				refresh: this.resizeHandler,
				load: this.resizeHandler,
				resize: this.resizeHandler,
				orientationchange: this.resizeHandler,
				scroll: this.scrollHandler
			});
		},
		makeCallback: function(name) {
			if (typeof this.options[name] === 'function') {
				var args = Array.prototype.slice.call(arguments);
				args.shift();
				this.options[name].apply(this, args);
			}
		}
	};
	$.fn.fixedBlock = function(options) {
		return this.each(function() {
			$(this).data('FixedBlock', new FixedBlock($.extend(options, { holder: this })));
		});
	};
}(jQuery));

/*
 * jQuery More Content plugin
 */
;(function($) {
	function MoreContent(options) {
		this.options = $.extend({
			ajaxClass: 'ajax-process',
			activeClass: 'active',
			ajaxHolder: '.ajax-holder',
			moreHolder: '.ajax-holder',
			items: '.filter-list li',
			moreBtnHolder: '.btn-holder',
			moreBtn: '.btn-more',
			animSpeed: 500,
			popupActiveClass: 'popup-active'
		}, options);
		this.init();
	}
	MoreContent.prototype = {
		init: function() {
			this.findElements();
			this.attachEvents();
			this.makeCallback('onInit', this);
		},
		findElements: function() {
			var self = this;
			this.area = $(this.options.holder).addClass(this.options.readyClass);
			this.items = this.area.find(this.options.items);
			this.ajaxHolder = this.area.find(this.options.ajaxHolder);
			this.moreBtnHolder = this.area.find(this.options.moreBtnHolder);
			this.moreBtn = this.area.find(this.options.moreBtn);
		},
		attachEvents: function() {
			var self = this;
			this.items.each(function() {
				var item = $(this);
				var link = item.children('a');
				var href = link.attr('href');
				link.on('click', function(e) {
					e.preventDefault();
					if (!item.hasClass(self.options.activeClass)) {
						self.items.removeClass(self.options.activeClass);
						item.addClass(self.options.activeClass);
						item.parents('li').addClass(self.options.activeClass);
						item.closest('.'+self.options.popupActiveClass).removeClass(self.options.popupActiveClass);
						self.loadContent(href, true);
					}
				});
			});
			this.moreBtn.on('click', function(e) {
				e.preventDefault();
				self.loadContent(self.moreBtn.attr('href'), false);
			});
		},
		loadContent: function(href, replace) {
			var self = this;
			this.area.addClass(this.options.ajaxClass);
			this.ajaxLoad(href, function(response) {
				if (response) {
					var newContent = $(response);
					var newHolder = newContent.find(self.options.moreHolder);
					var newBtnMore = newContent.find(self.options.moreBtn);
					if (newBtnMore && newBtnMore.length) {
						self.moreBtnHolder.slideDown(self.options.animSpeed);
						self.moreBtn.attr('href', newBtnMore.attr('href'));
					} else {
						self.moreBtnHolder.slideUp(self.options.animSpeed);
					}
					if (replace) {
						self.ajaxHolder.html(newHolder.html());
					} else {
						self.ajaxHolder.append(newHolder.html());
					}
					self.makeCallback('onLoad', replace);
				}
				self.area.removeClass(self.options.ajaxClass);
			});
		},
		ajaxLoad: function(url, callback) {
			$.ajax({
				type:'get',
				url:url,
				dataType: 'html',
				success:function(response) {
					callback(response);
				},
				error: function() {
					callback(false);
				}
			});
		},
		makeCallback: function(name) {
			if (typeof this.options[name] === 'function') {
				var args = Array.prototype.slice.call(arguments);
				args.shift();
				this.options[name].apply(this, args);
			}
		}
	};
	$.fn.moreContent = function(options) {
		return this.each(function() {
			$(this).data('MoreContent', new MoreContent($.extend(options, { holder: this })));
		});
	};
}(jQuery));

